import React, { useState } from 'react';
const App1 = () => {

    const [name, setName] = useState();

    const [lastName, setLastName] = useState();

    const [fullName, setFullName] = useState();

    const [lastNameN, setLastNameN] = useState();

    
    const inputEvent = (event) => {
      //  console.log(event.target.value);
        setName(event.target.value);
    }

    const inputEventTwo = (event) => {
        //  console.log(event.target.value);
        setLastName(event.target.value);
    }

           
    const onSubmits = (event) => {
        event.preventDefault();   // it removes the form refresh property.
        setFullName(name);
    }
        ;
   
    return (
        <>
            <div className="main_div">
            <form onSubmit = {onSubmits}>
                <div>
                <h1>Hello {fullName }</h1>
                        <input type="text" placeholder="Enter your first Name" onChange={inputEvent} value={name} />
                        <br />

                        <input type="text" placeholder="Enter your last Name" onChange={inputEventTwo} value={lastName} />
               
                        <button onClick={onSubmits}>Click Me üëç</button>

                </div>
             </form>
            </div>
       </>
    );

}
export default App1;
