import React, { useState } from 'react';
const App1 = () => {
    const purple = "#8e44ad";
    const [bg, setBg] = useState(purple);
    const [name, setName] = useState("Click Me");

    const bgChange = () => {
        let newBg = "#34495e";
        setBg(newBg);
        setName("Ouch!! ðŸ˜²"); 
       
    };
    const bgBack = () => {
        setBg(purple);
        setName("Ayyo!! ðŸ˜ ");
    }
    return (
        <>
            <div style = {{backgroundColor:bg }}>
                
                <button onMouseEnter={bgChange} onMouseLeave = {bgBack}>{name}</button>

        </div>    
      
       </>
    );

}
export default App1;
