import React, { useEffect, useState } from "react";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

// When render method called useEffect runs automatically
// due to pass [] empty array useEffect() runs one time only when you refresh the page
     // due to pass [num]  array useEffect() runs  when you click on  the first  button only



const App1 = () => {
    const [num, setNum] = useState(0);
   
    useEffect(() => {
       // alert("Button clicked");
        document.title = `you clicked me `
    }, [num]);    
    return (
        <>
          
            <button
                onClick={() => {
                    setNum(num + 1);
                }
                }
            >
                Click Me {num}
            </button>
            <br />
           
             
      </>
    );
};
export default App1;
